class MainActivity :
    BaseActivity<ActivityMainBinding, MainViewModel>(ActivityMainBinding::inflate) {

    private val TAG = "YingMing"
    private lateinit var mUser: User
    private lateinit var mProgressBar: ProgressBarLoading

    private lateinit var mMangaAdapter: AdapterManga
    private var mListManga = ArrayList<Manga>()

    override fun initListener() {
        binding.imvAvatar.setOnClickListener(this)
        binding.imvProfile.setOnClickListener(this)
        binding.imvFilter.setOnClickListener(this)
        binding.layoutReadContinue.setOnClickListener(this)
        binding.layoutToolBar.setOnClickListener(this)
    }

    override fun getViewModel(): Class<MainViewModel> {
        return MainViewModel::class.java
    }

    override fun initObserver() {
        viewModel.mListMangaLiveData.observe(this) {
            Log.d(TAG, "initObserver: ${it.size}")
            if (it.isNotEmpty()) {
                mProgressBar.hideLoading()
                mMangaAdapter.setListManga(it)
                mListManga = it
                binding.tvNoData.gone()
            } else {
                binding.tvNoData.visible()
            }
        }
    }

    override fun initDataDefault() {
        mProgressBar = ProgressBarLoading(this)
//        mUser = SharedPrefUtils.getLoginInformationUser(this)
