            "W0uptWWRzqXQUAwTQ94oIzldah22")
        binding.tvUser.text = mUser.name
        Glide.with(this).load(mUser.avatar).placeholder(R.drawable.img_avatar_default)
            .into(binding.imvAvatar)
        viewModel.getListManga(this)
        initRecycleView()
    }

    override fun onClick(v: View) {
        when (v) {
            binding.imvAvatar, binding.imvProfile, binding.layoutToolBar -> {
                startActivity(Intent(this, ProfileActivity::class.java))
            }
            binding.imvFilter -> {}
            binding.layoutReadContinue -> {}
        }
    }

