class ViewLiked : BaseView {

    private lateinit var mContext: Context
    private lateinit var mActivity: Activity
    private var checkFirstLaunchView: Boolean = false

    private lateinit var mMangaAdapter: AdapterManga
    private var mListManga = ArrayList<Manga>()
    private lateinit var binding: ViewLikedBinding

    constructor(context: Context) : super(context) {
        mContext = context
        initView(context, null)
    }

    constructor(context: Context, attrs: AttributeSet) : super(context, attrs) {
        mContext = context
        initView(context, attrs)
    }

    override fun initView(context: Context, attrs: AttributeSet?) {
        super.initView(context, attrs)
        binding = ViewLikedBinding.inflate(LayoutInflater.from(context), this, true)
    }

    override fun initViewModel(viewModel: ViewModel?) {
    }

    override fun initObserver(owner: LifecycleOwner?) {
    }

    override fun initDataDefault(activity: Activity?) {
        super.initDataDefault(activity)
        initRecycleView()
    }

    override fun openForTheFirstTime(activity: Activity?) {
        super.openForTheFirstTime(activity)
        mActivity = activity!!
        if (!checkFirstLaunchView) {
            checkFirstLaunchView = false
            initDataDefault(activity)
        }
    }

    override fun onClick(view: View) {
        when (view) {

        }
    }

    private fun initRecycleView() {
        mMangaAdapter = AdapterManga(mContext, mListManga) {
//            BookDAO().deleteBook(this, it) {
//                mLab4ViewModel.getListBook()
//                binding.pgLoading.gone()
//            }
        }
        mListManga.add(Manga("8",
            "Doraemon",
            "Tập 1",
            User("18","Bùi Xuân Tiến",
                "kaiyuan997@gmail.com",
                "data:image/png;base64,",
                "W0uptWWRzqXQUAwTQ94oIzldah22"),
            "data:image/jpeg;base64,",
            ArrayList()))
        binding.rcvListLiked.layoutManager = LinearLayoutManager(mContext)
        binding.rcvListLiked.setHasFixedSize(false)
        binding.rcvListLiked.isNestedScrollingEnabled = false
        binding.rcvListLiked.adapter = mMangaAdapter
    }
}